import {Component, OnDestroy, OnInit} from '@angular/core';
import {AuthService} from "./shared/services/auth.service";
import {Subscription} from "rxjs";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit, OnDestroy {
  title: string = 'app';
  loggedInSubscription: Subscription | undefined;
  loggedIn: boolean = false;
  id: number = 0;

  constructor(private authService: AuthService){}

  ngOnInit() {
    this.loggedInSubscription = this.authService.loggedIn$.subscribe(
      (loggedIn => { this.loggedIn = loggedIn; })
    );
    this.authService.userId$.subscribe(
      (id) => { this.id = id; }
    );
  }

  logout(){
    sessionStorage.clear();
    this.authService.logout();
  }

  ngOnDestroy() {
    this.loggedInSubscription?.unsubscribe();
  }

}
<nav>
  <ng-container *ngIf="loggedIn; then logged; else notLogged"></ng-container>
  <ng-template #logged>
    <ul>
      <li><a [routerLink]="'/'">Home</a></li>
      <li><a [routerLink]="'/browse'">Browse</a></li>
      <li><a [routerLink]="'/add'">Add item</a></li>
      <li><a [routerLink]="'/profile/' + id">Profile</a></li>
      <li><a (click)="logout()">Logout</a></li>
    </ul>
  </ng-template>
  <ng-template #notLogged>
    <ul>
      <li><a [routerLink]="'/'">Home</a></li>
      <li><a [routerLink]="'/add'">Browse</a></li>
      <li><a [routerLink]="'/signin'">Sign in</a></li>
      <li><a [routerLink]="'/signup'">Sign up</a></li>
    </ul>
  </ng-template>
</nav>
<div class="router">
  <router-outlet></router-outlet>
</div>
